package com.example.l3_7

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.l3_7.databinding.FragmentMainBinding
import com.example.l3_7.databinding.ItemIconBinding

class MainFragment : Fragment() {
    private lateinit var binding: FragmentMainBinding
    companion object{
    private val list = arrayListOf(
       iconModel("data:image/jpeg;base64,", "Anton","Dead","1"),
       iconModel("data:image/jpeg;base64,", "Petr","Alive","2"),
       iconModel("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR52ne6sWhTw0hSYev_WbHHdwSXCkXTmlbR-g&usqp=CAU", "Alex","Dead","3"),
       iconModel("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnshmgt-m76n49d-F77xDhD1BA2359sBgqRg&usqp=CAU", "Ilya","Alive","4"),
    )}
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentMainBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val adapter = iconAdapter(list,this::onClick)
        binding.rvIcon.adapter = adapter
    }
    private fun onClick(position: iconModel){
        findNavController().navigate(R.id.detailFragment, bundleOf(iconKeys.IconKet to position)) }

    }
